import { Component, Input, OnInit } from '@angular/core';
import { Project } from 'src/app/interfaces/project';
import { Ticket } from 'src/app/interfaces/ticket';
import { TicketService } from 'src/app/services/ticket/ticket.service';

@Component({
  selector: 'app-tickets-card',
  templateUrl: './tickets-card.component.html',
  styleUrls: ['./tickets-card.component.css']
})
export class TicketsCardComponent implements OnInit {

  constructor(public ticketService:TicketService) { }

  @Input()
  project!: Project;

  tickets!: Ticket[];

  pageNo=0;

  async ngOnInit(): Promise<void> {
    this.tickets = await this.ticketService.getTicketsOfProject(this.project.id,this.pageNo).toPromise();
  }
  public async nextTickets(){       
    if(this.tickets.length!=0){
      console.warn("click called");
      this.pageNo+=1;
      this.tickets = await this.ticketService.getTicketsOfProject(this.project.id,this.pageNo).toPromise();
    }
  }
  public async previousTickets(){
    if(this.pageNo!==0){
      this.pageNo-=1;
      console.warn("click called");
      this.tickets = await this.ticketService.getTicketsOfProject(this.project.id,this.pageNo).toPromise();
    }
  }

}
<div class="card">
  <div class="card-header card-header-warning">
    <h4 class="card-title">Tickets</h4>
  </div>
  <div class="card-body table-responsive">
    <table class="table table-hover">
      <thead class="text-warning">
        <th>ID</th>
        <th>Title</th>
        <th>Status</th>
        <th>Created</th>
        <th>Created By</th>
        <th>Assigned To</th>
        <th>Priority</th>
        <th>Tags</th>
      </thead>
      <tbody>
        <tr
          app-tickets-card-row
          *ngFor="let ticket of tickets"
          [ticket]="ticket"
        ></tr>
      </tbody>
    </table>
    <button
      type="button"
      rel="tooltip"
      title="Previous 10 Tickets"
      class="btn btn-primary btn-link btn-sm"
      (click)="previousTickets()"
    >
      <i class="material-icons">chevron_left</i>
    </button>
    <button
      type="button"
      rel="tooltip"
      title="Next 10 Tickets"
      class="btn btn-primary btn-link btn-sm"
      (click)="nextTickets()"
    >
      <i class="material-icons">chevron_right</i>
    </button>
  </div>
</div>
