import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Tag } from 'src/app/interfaces/tag';
import { Ticket } from 'src/app/interfaces/ticket';
import { TicketService } from 'src/app/services/ticket/ticket.service';

@Component({
  selector: 'app-tickets',
  templateUrl: './tickets.component.html',
  styleUrls: ['./tickets.component.css']
})
export class TicketsComponent implements OnInit {

  constructor(public ticketService:TicketService,public router: Router) { 
    const navigation  = this.router.getCurrentNavigation();
    if(navigation){
     this.tag = navigation.extras.state ? navigation.extras.state.tag : {id:0,tag:{} as any, tickets: {} as any};
   }
  }

  tickets!: Ticket[];

  pageNo=0;
  sortBy="created";
  getBy="all"

  tag: Tag = {id:0,tag:{} as any, tickets: {} as any};


  async ngOnInit(): Promise<void> {
    this.loadTickets();

  }

  public async nextTickets(){       
    if(this.tickets.length!=0){
      console.warn("click called");
      this.pageNo+=1;
      this.loadTickets();
    }
  }
  public async previousTickets(){
    if(this.pageNo!==0){
      this.pageNo-=1;
      console.warn("click called");
      this.loadTickets();
    }
  }

  public async loadTickets(){
    this.tickets = await this.ticketService.getTicketsOfProjectsWithUser(this.pageNo,this.sortBy,this.getBy,this.tag.id).toPromise();
  }

  public changeSortBy(sortBy:string){
    this.sortBy = sortBy;
    this.loadTickets();
  }
  public changeGetBy(getBy:string){
    this.getBy = getBy;
    this.tag.id=0;
    this.loadTickets();
  }
  public changeTag(tag:Tag){
    this.tag=tag;
    this.loadTickets();
  }

}
<div class="content">
  <div class="container-fluid">
    <div class="row">
      <div class="col-md-2">
        <div ngbDropdown class="d-inline-block">
          <button
            class="btn btn-outline-primary"
            id="priorityDropDown"
            ngbDropdownToggle
            type="button"
          >
            Sort By: {{ this.sortBy }}
          </button>
          <div ngbDropdownMenu aria-labelledby="priorityDropDown">
            <button ngbDropdownItem (click)="changeSortBy('created')">
              Date Created
            </button>
            <button ngbDropdownItem (click)="changeSortBy('priority')">
              Priority
            </button>
            <button ngbDropdownItem (click)="changeSortBy('deadline')">
              Deadline
            </button>
          </div>
        </div>
      </div>
      <div class="col-md-2">
        <div ngbDropdown class="d-inline-block">
          <button
            class="btn btn-outline-primary"
            id="priorityDropDown"
            ngbDropdownToggle
            type="button"
          >
            Get By:
            {{ this.tag.id == 0 ? this.getBy : "Getting Tickets With Tag" }}
          </button>
          <div ngbDropdownMenu aria-labelledby="priorityDropDown">
            <button ngbDropdownItem (click)="changeGetBy('all')">All</button>
            <button ngbDropdownItem (click)="changeGetBy('owned by')">
              Owned by you
            </button>
            <button ngbDropdownItem (click)="changeGetBy('assigned to')">
              Assigned to ticket
            </button>
            <button ngbDropdownItem (click)="changeGetBy('active')">
              Active
            </button>
            <button ngbDropdownItem (click)="changeGetBy('onhold')">
              On Hold
            </button>
            <button ngbDropdownItem (click)="changeGetBy('closed')">
              Closed
            </button>
          </div>
        </div>
      </div>
      <div class="col-md-2"></div>
    </div>
    <div class="row">
      <app-ticket-card
        class="col-lg-3 col-md-6 col-sm-6"
        *ngFor="let ticket of tickets"
        (onTagClicked)="changeTag($event)"
        [ticket]="ticket"
      ></app-ticket-card>
    </div>
    <button
      type="button"
      rel="tooltip"
      title="Previous 10 Tickets"
      class="btn btn-primary btn-link btn-sm btn-outline-primary"
      (click)="previousTickets()"
    >
      <i class="material-icons">chevron_left</i>
    </button>
    <button
      type="button"
      rel="tooltip"
      title="Next 10 Tickets"
      class="btn btn-primary btn-link btn-sm btn-outline-primary"
      (click)="nextTickets()"
    >
      <i class="material-icons">chevron_right</i>
    </button>
  </div>
</div>
