import { DatePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Ticket } from 'src/app/interfaces/ticket';
import { TicketHistory } from 'src/app/interfaces/ticket-history';
import { Comment } from 'src/app/interfaces/comment';
import { TicketService } from 'src/app/services/ticket/ticket.service';
import { NgForm } from '@angular/forms';
import { HttpErrorResponse } from '@angular/common/http';
import { UserServiceService } from 'src/app/services/user/user-service.service';
import { User } from 'src/app/interfaces/user';
import { Tag } from 'src/app/interfaces/tag';
import { TagService } from 'src/app/services/tag/tag.service';
import { NgbDropdownConfig } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-ticket',
  templateUrl: './ticket.component.html',
  styleUrls: ['./ticket.component.css'],
  providers: [NgbDropdownConfig] 
})
export class TicketComponent implements OnInit {

  ticket!: Ticket;
  ticketHistory!: TicketHistory[];
  ticketHistoryPageNo=0;

  ticketComments!: Comment[];
  commentsPageNo=0;

  editTicketButtonDisabled=false;

  allTags!: Tag[];
  selectedTags!: Tag[];


  constructor(public router:Router, public datepipe:DatePipe,public ticketService:TicketService, public userService:UserServiceService,
    public tagService: TagService) {
    const navigation  = this.router.getCurrentNavigation();
    if(navigation){
     this.ticket = navigation.extras.state ? navigation.extras.state.ticket : 0;
   }

   //On page refresh, the state is lost along with the object so I reroute the user back to tickets 
   if(this.ticket.id==null){
     this.router.navigateByUrl("/tickets");
   }
  }

  async ngOnInit(): Promise<void> {
    this.ticketHistory = await this.ticketService.getTicketHistory(this.ticket.id,this.ticketHistoryPageNo).toPromise();
    this.ticketComments= await this.ticketService.getTicketComments(this.ticket.id,this.commentsPageNo).toPromise();
    this.allTags = await this.tagService.getTags().toPromise();
    const username = localStorage.getItem("username");
    if(this.ticket.submitUser.userName!==username){
      this.editTicketButtonDisabled=true;
    }
  }
  public openEditTicket(){
    this.router.navigateByUrl('tickets/ticket/edit', { state: { ticket: this.ticket } });
  }

  public async nextTicketHistory(){       
    if(this.ticketHistory.length!=0){
      console.warn("click called");
      this.ticketHistoryPageNo+=1;
      this.ticketHistory = await this.ticketService.getTicketHistory(this.ticket.id,this.ticketHistoryPageNo).toPromise();
    }
  }
  public async previousTicketHistory(){
    if(this.ticketHistoryPageNo!==0){
      this.ticketHistoryPageNo-=1;
      console.warn("click called");
      this.ticketHistory = await this.ticketService.getTicketHistory(this.ticket.id,this.ticketHistoryPageNo).toPromise();
    }
  }


  public async nextComments(){       
    if(this.ticketComments.length!=0){
      console.warn("click called");
      this.commentsPageNo+=1;
      this.ticketComments= await this.ticketService.getTicketComments(this.ticket.id,this.commentsPageNo).toPromise();
    }
  }
  public async previousComments(){
    if(this.commentsPageNo!==0){
      this.commentsPageNo-=1;
      console.warn("click called");
      this.ticketComments= await this.ticketService.getTicketComments(this.ticket.id,this.commentsPageNo).toPromise();
    }
  }





  public async onAddComment(addCommentForm: NgForm):Promise<void>{
    const comment = addCommentForm.form.get('comment_message')?.value;
    let userId= localStorage.getItem("user_id") ? Number(localStorage.getItem("user_id")) : 0;

    let newComment: Comment = {
      message: comment,
      ticket: this.ticket,
      id: 0,
      user: {} as any
    }

    this.ticketService.addComment(this.ticket.id,userId,newComment).subscribe(
      (response: Comment)=>  {
        alert("Comment added")
        this.ticketComments.push(response);

      },
      (error: HttpErrorResponse) =>{
        alert(error.message);
      }    
    )
  }

  userToBeAdded!: User;
  public async onAssignUser(assignUserForm: NgForm):Promise<void>{
    const username = assignUserForm.form.get('assign_username')?.value;

    this.userToBeAdded =  await this.userService.getUserByName(username).toPromise();

    if(this.userToBeAdded?.id){
      console.warn(`User to be assigned to ticket ID: ${this.userToBeAdded.id}`);

      this.ticketService.assignUserToTicket(this.userToBeAdded.id,this.ticket.id).subscribe(
        (response: any)=>  {
          alert(`User: ${this.userToBeAdded.userName} Assigned to Ticket`);

          //refresh
          const currentUrl = this.router.url;
          this.router.navigateByUrl('/', {skipLocationChange: true}).then(() => {
          this.router.navigate([currentUrl],{ state: { ticket: response } });
          });
         },
         (error: HttpErrorResponse) =>{
           alert(error.message);
         }    
      );
    }else{alert("User not found")}
  }


}
<div class="content">
    <div class="container-fluid">
      <div class="row">
        <div class="col-md-8">
          <div class="card">
            <div class="card-header card-header-primary">
              <h4 class="card-title">{{ticket.ticketTitle}}</h4>
            </div>
            <div class="card-body">
              <div class="card-title">
                <h4>Description</h4>
              </div>
              <div class="card-title">
                <p>
                  {{ticket.ticketDesc}}
                </p>
              </div>
            </div>
          </div>
          <div class="card">
            <div class="card-header card-header-warning">
              <h4 class="card-title">Ticket History</h4>
            </div>
            <div class="card-body table-responsive">
              <table class="table table-hover">
                <thead class="text-warning">
                  <th>Old Title</th>
                  <th>Old Description</th>
                  <th>Date Changed</th>
                </thead>
                <tbody>
                  <tr *ngFor="let history of ticketHistory">
                    <td>{{history.oldTitle}}</td>
                    <td>{{history.oldDesc}}</td>
                    <td>{{this.datepipe.transform(history.dateChanged, "yyyy-MM-dd")}}</td>
                  </tr>                 
                </tbody>
              </table>
              <button
                type="button"
                rel="tooltip"
                title="Previous 10 History"
                class="btn btn-primary btn-link btn-sm"
                (click)="previousTicketHistory()"
              >
                <i class="material-icons">chevron_left</i>
              </button>
              <button
                type="button"
                rel="tooltip"
                title="Next 10 History"
                class="btn btn-primary btn-link btn-sm"
                (click)="nextTicketHistory()"
              >
                <i class="material-icons">chevron_right</i>
              </button>
            </div>
          </div>
        </div>

        <div class="card card-stats col-md-4">
          <div class="card-header card-header-primary card-header-icon">
            <div class="card-icon">
              <i class="material-icons">bug_report</i>
            </div>
            <h3 class="card-title">Ticket Details</h3>
          </div>

          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Start date: {{this.datepipe.transform(this.ticket.ticketCreated, "yyyy-MM-dd")}}</a>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Priority: {{ticket.ticketPriority.priorityType}}</a>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Owner: {{ticket.submitUser.userName}}</a>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Assigned user: {{ticket?.assignedUser?.userName}}</a>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Deadline: {{this.datepipe.transform(this.ticket.deadline, "yyyy-MM-dd")}}</a>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer container-fluid">
            <a>Tags: </a>
            <div class="stats row col-md-12 container-fluid">

            <button
            type="button"
            rel="tooltip"
            title="View tickets with this tag"
            class="btn btn-info btn-link btn-sm "
            *ngFor="let tag of ticket.tags"
          >
          <a >{{tag.tag}} </a>
          </button>
          </div>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Add Tags: </a>
            <button
              type="button"
              rel="tooltip"
              title="Edit Ticket"
              class="btn btn-primary btn-link btn-sm"
              [disabled]="this.editTicketButtonDisabled"

            >
              <i class="material-icons">add</i>
            </button>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">
            <a>Edit Ticket: </a>
            <button
              type="button"
              rel="tooltip"
              title="Edit Ticket"
              class="btn btn-primary btn-link btn-sm"
              [disabled]="this.editTicketButtonDisabled"
              (click)="openEditTicket()"
            >
              <i class="material-icons">edit</i>
            </button>
          </div>
          <div class="card-header card-header-success card-header-icon"></div>
          <div class="card-footer">









            <div class="row">
              <div class="col">
                <div ngbDropdown class="d-inline-block">
                  <button class="btn btn-outline-primary tn btn-primary btn-link btn-sm" id="assignUserDropdownForm" ngbDropdownToggle
                  rel="tooltip"
                    title="Assign User To Ticket" [disabled]="this.editTicketButtonDisabled">
                    <i class="material-icons">edit</i>
                  </button>
                  <div ngbDropdownMenu aria-labelledby="dropdownForm1">
                    <form class="px-4 py-3"  #assignUserForm="ngForm" (ngSubmit)="onAssignUser(assignUserForm)">
                      <div class="form-group">
                        <label for="assignUserDropdownFormUsername">User's Name</label>
                        <input ngModel name="assign_username" #input required type="text" class="form-control" id="assignUserDropdownFormUsername" placeholder="username" autocomplete="username">
                      </div>

                      <button [disabled]="!assignUserForm.valid" type="submit" class="btn btn-primary">Assign User</button>
                    </form>

                  </div>
                </div>
              </div>
            </div>












            <a>: Assign User To Ticket </a>
































          </div>
          <div class="card-footer ">









            <div class="row">
              <div class="col-md-12">
                <div ngbDropdown class="d-inline-block">
                  <button class="btn btn-outline-primary tn btn-primary btn-link btn-sm" id="assignUserDropdownForm" ngbDropdownToggle
                  rel="tooltip"
                    title="Assign User To Ticket" [disabled]="this.editTicketButtonDisabled">
                    <i class="material-icons">add</i>
                  </button>
                  <div ngbDropdownMenu aria-labelledby="dropdownFormTags" class="row dropdown-menu-moded">
                    <div class="card-footer container-fluid">
                      <div class="stats row col-md-12 container-fluid">
                          <a >  Tags: </a>

                          <button
                        type="button"
                        rel="tooltip"
                        title="View tickets with this tag"
                        class="btn btn-info btn-link btn-sm "
                        *ngFor="let tag of this.allTags"
                      >
                      <a >{{tag.tag}} </a>
                      </button>
                        </div>

                  </div>

                  </div>
                </div>
              </div>
            </div>












            <a>: Add Tags To Ticket </a>
































          </div>
        </div>

      </div>
      <div class="row">


        <div class="card">
            <div class="card-header card-header-primary">
              <h4 class="card-title">Comments</h4>
            </div>
            <div class="card-body">
                <!-- For each comment-->
                <div class="row" *ngFor="let comment of ticketComments">
                    <div class="card card-stats col-md-12">
                        <div class="card-header card-header-primary card-header-icon">
                          <div class="card-icon ">
                            <i class="material-icons">person</i>
                          </div>
                          <h4 class="card-title">User: {{comment.user.userName}}</h4>
                        </div>

                        <div class="card-header card-header-success card-header-icon"></div>
                        <div class="card-footer">
                          <a>{{comment.message}}</a>
                        </div>
                        <div class="card-header card-header-success card-header-icon"></div>
                        <div class="card-footer">

                        </div>
                      </div>
                </div>

                <div class="row">
                    <div class="col col-md-12">
                      <div ngbDropdown class="d-inline-block col-md-12">
                        <button class="btn btn-outline-primary tn btn-primary btn-link btn-sm" id="assignUserDropdownForm" ngbDropdownToggle
                        rel="tooltip"
                          title="Add comment">
                          <i class="material-icons">add</i>
                        </button>
                        <div ngbDropdownMenu aria-labelledby="dropdownForm1" class="col-md-12">
                          <form class="px-4 py-3" #addCommentForm="ngForm" (ngSubmit)="onAddComment(addCommentForm)">
                            <div class="form-group">
                              <label for="assignUserDropdownFormUsername">Type your comment</label>
                              <textarea ngModel name="comment_message" #input required cols="40" rows="5" type="text" class="form-control" id="assignUserDropdownFormUsername" placeholder="comment (max 800char)" maxlength="800"></textarea>
                            </div>

                            <button [disabled]="!addCommentForm.valid" type="submit" class="btn btn-primary">Submit</button>
                          </form>

                        </div>
                        <button
                        type="button"
                        rel="tooltip"
                        title="Previous 10 Comments"
                        class="btn btn-primary btn-link btn-sm"
                        (click)="previousComments()"
                      >
                        <i class="material-icons">chevron_left</i>
                      </button>
                      <button
                        type="button"
                        rel="tooltip"
                        title="Next 10 Comments"
                        class="btn btn-primary btn-link btn-sm"
                        (click)="nextComments()"
                      >
                        <i class="material-icons">chevron_right</i>
                      </button>
                      </div>

                    </div>

                  </div>


            </div>
        </div>



      </div>
    </div>
  </div>
