import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Priority } from 'src/app/interfaces/priority';
import { Ticket } from 'src/app/interfaces/ticket';
import { PriorityService } from 'src/app/services/priority/priority.service';
import { TicketService } from 'src/app/services/ticket/ticket.service';
import {NgbDateStruct, NgbCalendar, NgbDate} from '@ng-bootstrap/ng-bootstrap';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-edit-ticket',
  templateUrl: './edit-ticket.component.html',
  styleUrls: ['./edit-ticket.component.css'],
  styles: [`
  .custom-day {
    text-align: center;
    padding: 0.185rem 0.25rem;
    display: inline-block;
    height: 2rem;
    width: 2rem;
  }
  .custom-day.focused {
    background-color: #e6e6e6;
  }
  .custom-day.range, .custom-day:hover {
    background-color: rgb(2, 117, 216);
    color: white;
  }
  .custom-day.faded {
    background-color: rgba(2, 117, 216, 0.5);
  }
`]
})
export class EditTicketComponent implements OnInit {

  ticket !: Ticket;
  priorities!:Priority[];
  selectedPriority!: Priority;
  hoveredDate: NgbDate | null = null;

  fromDate!: NgbDate;
  toDate: NgbDate | null = null;
  constructor(public router:Router, public priorityService:PriorityService, public ticketService:TicketService,private calendar: NgbCalendar) { 
    const navigation  = this.router.getCurrentNavigation();
     if(navigation){
      this.ticket = navigation.extras.state ? navigation.extras.state.ticket : 0;
    }



    //On page refresh, the state is lost along with the object so I reroute the user back to tickets 
    if(this.ticket.id==null){
      this.router.navigateByUrl("/tickets");
    }

  }

  async ngOnInit(): Promise<void> {
    this.priorities = await this.priorityService.getPriorities().toPromise();
    this.fromDate = this.calendar.getToday();
    this.toDate = this.calendar.getNext(this.calendar.getToday(), 'd', 10);
    this.selectedPriority = this.ticket.ticketPriority;
    this.selectedStatus = this.ticket.status;
  }
  public setPriority(priority:Priority):void{
    this.selectedPriority=priority;


  }
  selectedStatus="";
  public setStatus(status:string):void{
    this.selectedStatus=status;


  }

  public async onUpdateTicket(updateTicketForm: NgForm):Promise<void>{
    const ticketTitle = updateTicketForm.form.get('ticket_title')?.value;
    const ticketDesc = updateTicketForm.form.get('ticket_desc')?.value;
    //apparently months start at 0 in js so we need to take 1 away....
    const startedDate = new Date(this.fromDate.year,this.fromDate.month-1,this.fromDate.day);
    if(this.toDate!==null){


      const deadline = new Date(this.toDate.year,this.toDate.month-1,this.toDate.day);
      console.warn(`Ticket title: ${ticketTitle}\n Ticket desc: ${ticketDesc}\n deadline: ${deadline}\n date started: ${startedDate}
      \n ticker prio: ${this.selectedPriority.priorityType}\n status: ${this.selectedStatus}\n project: ${this.ticket.project.id}`);

      this.ticket.ticketTitle= ticketTitle;
      this.ticket.ticketDesc=ticketDesc;
      this.ticket.ticketCreated=startedDate;
      this.ticket.deadline=deadline;
      this.ticket.status=this.selectedStatus;
      this.ticket.ticketPriority=this.selectedPriority;

      this.ticketService.updateTicket(this.ticket.id,this.selectedPriority.id,this.ticket).subscribe(
        (response: Ticket)=>  {
          alert("Ticket Updated")
          this.router.navigateByUrl('tickets/ticket', { state: { ticket: response } });

        },
        (error: HttpErrorResponse) =>{
          alert(error.message);
        }    
      )



    }else{alert("Deadline not selected");}





  }















  onDateSelection(date: NgbDate) {
    if (!this.fromDate && !this.toDate) {
      this.fromDate = date;
    } else if (this.fromDate && !this.toDate && date.after(this.fromDate)) {
      this.toDate = date;
    } else {
      this.toDate = null;
      this.fromDate = date;
    }
  }

  isHovered(date: NgbDate) {
    return this.fromDate && !this.toDate && this.hoveredDate && date.after(this.fromDate) && date.before(this.hoveredDate);
  }

  isInside(date: NgbDate) {
    return this.toDate && date.after(this.fromDate) && date.before(this.toDate);
  }

  isRange(date: NgbDate) {
    return date.equals(this.fromDate) || (this.toDate && date.equals(this.toDate)) || this.isInside(date) || this.isHovered(date);
  }

}

<div class="content">
    <div class="container-fluid">
      <div class="row">
        <div class="col-md-12">
          <div class="card">
            <div class="card-header card-header-primary">
              <h4 class="card-title">Edit Ticket</h4>
              <p class="card-category">{{ticket.ticketTitle}}</p>
            </div>
            <div class="card-body">
              <form #updateTicketForm="ngForm"  (ngSubmit)="onUpdateTicket(updateTicketForm)">
                <div class="row">
                  <div class="col-md-12">
                    <div class="form-group">
                      <label class="bmd-label-floating">Ticket Title</label>
                      <input type="text" class="form-control" [(ngModel)]="this.ticket.ticketTitle" ngModel name="ticket_title" #input required maxlength="50"/>
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-12">
                    <div class="form-group">
                      <label class="bmd-label-floating"
                        >Ticket Description</label
                      >
                      <input type="text" class="form-control"  [(ngModel)]="this.ticket.ticketDesc" ngModel name="ticket_desc" #input required maxlength="1000"/>
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-2">
                    <div ngbDropdown class="d-inline-block">
                      <button
                        class="btn btn-outline-primary"
                        id="priorityDropDown"
                        ngbDropdownToggle
                        type="button"
                      >
                        Status
                      </button>
                      <div ngbDropdownMenu aria-labelledby="priorityDropDown">
                        <button ngbDropdownItem (click)="setStatus('OPEN')">
                          Open
                        </button>
                        <button ngbDropdownItem (click)="setStatus('CLOSED')">
                          Closed
                        </button>
                        <button ngbDropdownItem (click)="setStatus('ONHOLD')">
                          On Hold
                        </button>
                      </div>
                    </div>
                  </div>
                  <div class="col-md-2 center" align-items="center">
                    <label
                      class="bmd-label-floating center"
                      text-align="center"
                      vertical-align="middle"
                      >Ticket Status: {{this.selectedStatus.toLowerCase()}}
                    </label>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-2">
                    <div ngbDropdown class="d-inline-block">
                      <button
                        class="btn btn-outline-primary"
                        id="priorityDropDown"
                        ngbDropdownToggle
                        type="button"
                      >
                        Priority
                      </button>
                      <div ngbDropdownMenu aria-labelledby="priorityDropDown">
                        <button ngbDropdownItem *ngFor="let prio of priorities" (click)="setPriority(prio)" type="button">
                          Type: {{prio.priorityType}} | Importance: {{prio.importance}} (higher the more important)
                        </button>
                      </div>
                    </div>
                  </div>
                  <div class="col-md-4 center" align-items="center">
                    <label
                      class="bmd-label-floating center"
                      text-align="center"
                      vertical-align="middle"
                      >Ticket Priority: {{this?.selectedPriority?.priorityType}} <br> Importance level:  {{this?.selectedPriority?.importance}}
                    </label>
                  </div>
                  <div class="col-md-2 center" align-items="center">

                  </div>


                </div>
                <div class="row">
                    <div class="col-md-12">
                      <div class="form-group">
                        <label class="bmd-label-floating"
                          >Ticket Start & Deadline</label
                        >
                        <div></div>
                        <ngb-datepicker class="btn btn-primary btn-link btn-sm" #dp ngModel name="ticket_deadline"
                        [displayMonths]="2" [dayTemplate]="t" outsideDays="hidden" (dateSelect)="onDateSelection($event)"></ngb-datepicker>
                        <ng-template #t let-date let-focused="focused">
                          <span class="custom-day btn btn-primary btn-link btn-sm"
                                [class.focused]="focused"
                                [class.range]="isRange(date)"
                                [class.faded]="isHovered(date) || isInside(date)"
                                (mouseenter)="hoveredDate = date"
                                (mouseleave)="hoveredDate = null">
                            {{ date.day }}
                          </span>
                        </ng-template>
                        <hr>
                        <pre class="text-primary">From: {{this.fromDate.year}}/{{this.fromDate.month}}/{{this.fromDate.day}} To: {{this?.toDate?.year}}/{{this?.toDate?.month}}/{{this?.toDate?.day}}
                        </pre>
                      </div>
                    </div>
                  </div>
                <button  [disabled]="!updateTicketForm.valid" type="submit" class="btn btn-primary pull-right">
                  Update Ticket
                </button>
                <div class="clearfix"></div>
              </form>
            </div>
          </div>
        </div>

        <!--<div class="card card-stats col-md-4">
          </div>-->
      </div>
    </div>
  </div>
