import { DatePipe } from '@angular/common';
import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Ticket } from 'src/app/interfaces/ticket';
import { TicketService } from 'src/app/services/ticket/ticket.service';

@Component({
  selector: 'app-ticket-card',
  templateUrl: './ticket-card.component.html',
  styleUrls: ['./ticket-card.component.css']
})
export class TicketCardComponent implements OnInit {

  @Input()
  ticket!: Ticket;

  commentCount=0;
  currentUsername = localStorage.getItem("username");

  today:Date = new Date();



  constructor(public ticketService:TicketService,public router: Router, public datepipe:DatePipe, public date:DateConstructor) { }

  async ngOnInit(): Promise<void> {
    this.commentCount = await this.ticketService.getCommentsCountForTicket(this.ticket.id).toPromise();

    if(this.date.parse(this.ticket?.deadline.toString())<this.date.parse(this.today.toString())){
      console.warn("pog");
    }
  }
  public openTicket(){
    this.router.navigateByUrl('tickets/ticket', { state: { ticket: this.ticket } });

  }

}
