import { Component, OnInit } from '@angular/core';
import { NgxChartsModule, LegendPosition } from '@swimlane/ngx-charts';
import { BarchartItem } from 'src/app/interfaces/statInterfaces/barchart-items';
import { TicketService } from 'src/app/services/ticket/ticket.service';

@Component({
  selector: 'app-graph-card',
  templateUrl: './graph-card.component.html',
  styleUrls: ['./graph-card.component.scss']
})
export class GraphCardComponent implements OnInit {


  items!: BarchartItem[];

  view: [number,number] = [400, 300];

  // options
  showXAxis = true;
  legendPosition = LegendPosition.Below;
  showYAxis = true;
  gradient = false;
  showLegend = false;
  showXAxisLabel = false;
  showYAxisLabel = false;

  constructor(public ticketService:TicketService) { 
  }

  async ngOnInit(): Promise<void> {
    this.items = await this.ticketService.getTicketsCountByPriority().toPromise();

    alert(this.items.length)
  }
  onSelect(event: any) {
    console.log(event);
  }

}
<div class="card">
  <div class="card-header card-header-success">
    <div class="areachart-wrapper">
      <ngx-charts-bar-vertical
        [view]="view"
        [results]="items"
        [gradient]="gradient"
        [xAxis]="showXAxis"
        [yAxis]="showYAxis"
        [legend]="showLegend"
        [showXAxisLabel]="showXAxisLabel"
        [showYAxisLabel]="showYAxisLabel"
        (select)="onSelect($event)"
        [legendPosition]="legendPosition"
      >
      </ngx-charts-bar-vertical>
    </div>
  </div>
  <div class="card-body">
    <h4 class="card-title">card Title</h4>
    <p class="card-category">card category</p>
  </div>
  <div class="card-footer">
    <div class="stats">card footer</div>
  </div>
</div>
