import { Component, OnInit } from '@angular/core';
import { User } from 'src/app/interfaces/user';
import { UserServiceService } from 'src/app/services/user/user-service.service';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent implements OnInit {


  users!: User[];
  pageNo=0;

  constructor(public userService:UserServiceService) { }

  async ngOnInit(): Promise<void> {
    this.users = await this.userService.getUsers(this.pageNo).toPromise();
  }

  public async nextUsers(){       
    if(this.users.length!=0){
      console.warn("click called");
      this.pageNo+=1;
      this.users = await this.userService.getUsers(this.pageNo).toPromise();
    }
  }
  public async previousUsers(){
    if(this.pageNo!==0){
      this.pageNo-=1;
      console.warn("click called");
      this.users = await this.userService.getUsers(this.pageNo).toPromise();
    }
  }

}
<div class="content">
    <div class="container-fluid">
      <div class="row">
        <div class="col-md-2">
          <div ngbDropdown class="d-inline-block">
            <button
              class="btn btn-outline-primary"
              id="priorityDropDown"
              ngbDropdownToggle
              type="button"
            >
              Sort By:
            </button>
            <div ngbDropdownMenu aria-labelledby="priorityDropDown">
              <button ngbDropdownItem>Priority</button>
              <button ngbDropdownItem>Date Created</button>
              <button ngbDropdownItem>Last Active</button>
            </div>
          </div>
        </div>
      </div>
      <div class="row">

          <app-user-card class=" col-md-5 col-sm-2" *ngFor="let user of users" [user]="user"></app-user-card>

      </div>
      <div class="row">
        <button
        type="button"
        rel="tooltip"
        title="Previous 10 Projects"
        class="btn btn-primary btn-link btn-sm  btn-outline-primary"
        (click)="previousUsers()"
      >
        <i class="material-icons">chevron_left</i>
      </button>
      <button
        type="button"
        rel="tooltip"
        title="Next 10 Projects"
        class="btn btn-primary btn-link btn-sm  btn-outline-primary"
        (click)="nextUsers()"
      >
        <i class="material-icons">chevron_right</i>
      </button>

      </div>
    </div>
  </div>
