import { Component, Input, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Priority } from 'src/app/interfaces/priority';

@Component({
  selector: 'app-priority-card',
  templateUrl: './priority-card.component.html',
  styleUrls: ['./priority-card.component.css']
})
export class PriorityCardComponent implements OnInit {

  @Input()
  priority!: Priority;

  value!: number;
  constructor() { }

  ngOnInit(): void {
    this.value = this.priority.importance;
  }
  public async onUpdateTag(updateTagForm: NgForm):Promise<void>{
    const priorityname = updateTagForm.form.get('priority_name')?.value;
    const prioritylevel = updateTagForm.form.get('priority_level')?.value;
    alert(`Priority name: ${priorityname} Priority level: ${prioritylevel}`);

  }

}
<div class="card card-stats">
    <div class="card-header card-header-primary card-header-icon">
      <div class="card-icon">
        <i class="material-icons">priority_high</i>
      </div>
      <p class="card-category">Priority:</p>
      <h3 class="card-title">
        {{priority.priorityType}}
      </h3>
    </div>
    <div class="card-header card-header-success card-header-icon"></div>
    <div class="card-footer">      
        <a >Priority level: {{priority.importance}}</a>
    </div>
    <div class="card-header card-header-success card-header-icon"></div>
    <div class="card-footer">      
      <div ngbDropdown class="d-inline-block">
        <button class="btn btn-outline-primary tn btn-primary btn-link btn-sm" id="assignUserDropdownForm" ngbDropdownToggle
        rel="tooltip"
          title="Assign User To Ticket">
          <i class="material-icons">edit</i>
        </button>
        <div ngbDropdownMenu aria-labelledby="dropdownFormTag">
          <form class="px-4 py-3"  #updatePriorityForm="ngForm" (ngSubmit)="onUpdateTag(updatePriorityForm)">
            <div class="form-group">
              <label for="assignUserDropdownFormUsername">New Priority Name</label>
              <input ngModel name="priority_name" #input required type="text" class="form-control" placeholder="Priority Name..." >
            </div>
            <div class="form-group">
                <label for="assignUserDropdownFormUsername">New Priority Level</label>
               <ng-number-picker ngModel #importance name="priority_level"  [max]="10" [min]="1" [value]="importance"></ng-number-picker>
            </div>

            <button [disabled]="!updatePriorityForm.valid" type="submit" class="btn btn-primary">Update Priority</button>
          </form>

        </div>
      </div>
    </div>

  </div>
