import { HttpErrorResponse } from '@angular/common/http';
import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Project } from 'src/app/interfaces/project';
import { ProjectUserRoleLink } from 'src/app/interfaces/project-user-role-link';
import { ProjectService } from 'src/app/services/project/project.service';

@Component({
  selector: 'app-project-card',
  templateUrl: './project-card.component.html',
  styleUrls: ['./project-card.component.css']
})
export class ProjectCardComponent implements OnInit {


  constructor(public projectService: ProjectService, public router: Router) { }

  @Input()
  project!: Project;


  stats: number[] = [];

  async ngOnInit(): Promise<void> {
    this.stats= await this.projectService.getProjectStats(this.project.id).toPromise();
  }
    //adds user with fixed owner role, just for demo's sake to easily access stuff
    public addUserToProject(){
    const userId= localStorage.getItem("user_id");

      this.projectService.addUserToProject(Number(userId),this.project.id,1).subscribe(
        (response: any)=>  {
          console.warn("User added to project with ID: "+this.project.id+"\n As role: "+response.role.roleType+"\n With access level: "+response.role.accessLevel);
        },
        (error: HttpErrorResponse) =>{
          alert(error.message);
        }    
      )
    }

  public openProject(){
    this.router.navigateByUrl('./project', { state: { projectId: this.project.id } });
  }

}
<div class="card card-stats">
    <div class="card-header card-header-danger card-header-icon">
      <div class="card-icon car" [ngClass]="{'card-header card-header-success card-header-icon' : this.project.projectOpen==true, 'card-header card-header-danger card-header-icon': this.project.projectOpen==false}">
        <i class="material-icons">assignment</i>
      </div>
      <p class="card-category">Project: 
      {{
        project.projectOpen ? 'Open':'Closed'

      }}</p>
      <h3 class="card-title">
        {{this.project.projectName}}
      </h3>
    </div>
    <div class="card-footer">
        <div class="stats">
            <a >Open Tickets: {{this.stats[0]}}</a>
          </div>

    </div>
    <div class="card-footer">
        <div class="stats">
            <a >Closed Tickets: {{this.stats[1]}}</a>
          </div>

    </div>
    <div class="card-footer">
        <div class="stats text-warning">
            <a >Your Assigned Tickets: {{this.stats[2]}}</a>
          </div>

    </div>
    <div class="card-footer">
        <div class="stats">
            <a >Priority: {{this.project.projectPriority.priorityType}} && Level: {{this.project.projectPriority.importance}}</a>
          </div>

    </div>
    <div class="card-footer">

        <button
          type="button"
          rel="tooltip"
          title="View project"
          class="btn btn-primary btn-link btn-sm" 
          (click)="openProject()"
        >
          <i class="material-icons" >visibility</i>
        </button>
        <button
          type="button"
          rel="tooltip"
          title="Add yourself to project (adds you as a owner for demo's sake)"
          class="btn btn-primary btn-link btn-sm"
          (click)="addUserToProject()"
        >
          <i class="material-icons">add</i>
        </button>
      </div>
  </div>
